mapper.update:
UPDATE
	dice_draw d
SET
   d.result = #{result}, d.end_time=#{end_time}
WHERE
    d.id = #{id}

mapper.select:
SELECT * FROM dice_draw WHERE 1 = 1
AND end_time is null
order by id desc limit ${beginIndex},${pageSize}

SELECT bet, sum(bet_value) as bet_value FROM dice_bet WHERE 1 = 1
and term=${term}
group by bet
