mapper.update:
UPDATE
	member m
SET
   m.sum = #{sum} 
WHERE
    m.mid = #{mid}

mapper.select:
SELECT sn.snid, sn.mid, sn.bet_time, sn.number, sn.type, sn.league, sn.team_h, sn.team_c, sn.bet, sn.score, sn.bet_type, sn.occasion, sn.ior_type, sn.ior_ratio, sn.state, sn.money, sn.valid_money, sn.win_lose, m.sum FROM member m RIGHT JOIN single_note sn ON m.mid = sn.mid WHERE 1 = 1
AND state = #{state}
AND bet_time LIKE CONCAT(#{betTime},'%')

SELECT fr.frid, fr.mid, fr.time, fr.number, fr.type, fr.money, fr.state, fr.remark, m.bank_number FROM member m RIGHT JOIN fund_record fr ON m.mid = fr.mid WHERE 1 = 1
AND fr.mid = #{mid}
AND fr.type = #{type}
AND fr.state = #{state}
AND #{beginTime} <= fr.time <= #{endTime}
